jQuery.fn.combobox=function(a){return this.each(function(){var c=this;a=a?a:{};jQuery(this).typeahead({source:a.source,highlighter:function(b){return"<div>"+b+"</div>"},matcher:function(b){return this.query=="*"?true:b.indexOf(this.query)>=0}});jQuery(this).wrap('<div class="input-append">').after('<div class="btn-group"><a class="btn dropdown-toggle"><span class="caret"></span></a></div>').next().click(function(){setTimeout(function(){jQuery(c).val("*").typeahead("lookup").val("").focus()},300)})})};
jQuery.fn.selectcombobox=function(){return this.each(function(){var a=jQuery(this),c=[];jQuery("option",this).each(function(d){c[d]=jQuery(this).text()});var b="combopop"+Math.random().toString(36).substring(7);a.after('<input type="text" autocomplete="off" class="'+b+" "+(a.attr("class")||"")+'" value="'+jQuery("option:selected",this).text()+'" placeholder="'+(a.attr("placeholder")||"")+'" id="'+(a.attr("id")||"")+'" name="'+a.attr("name")+'">').remove();jQuery("."+b).combobox({source:c})})};
